// Add validation results to original contact data
const originalContact = $json;

// Create enhanced contact record
const enhancedContact = {
  // Original data
  first_name: originalContact.first_name || '',
  last_name: originalContact.last_name || '',
  email: originalContact.email || '',
  company: originalContact.company || '',
  job_title: originalContact.job_title || '',
  phone: originalContact.phone || '',

  // Validation results
  email_valid: originalContact.deliverable || false,
  email_score: originalContact.score || 0,
  validation_reason: originalContact.reason || '',

  // Quality indicators
  is_disposable: originalContact.disposable || false,
  is_role_based: originalContact.role || false,

  // Metadata
  validated_date: new Date().toISOString(),
  list_source: 'bulk_upload'
};

return enhancedContact;

// Generate summary report
const validEmails = $input.first().json;
const invalidEmails = $input.all()[1]?.json || [];

// Calculate statistics
const totalEmails = (Array.isArray(validEmails) ? validEmails.length : 1) + 
                   (Array.isArray(invalidEmails) ? invalidEmails.length : (invalidEmails.length > 0 ? 1 : 0));
const validCount = Array.isArray(validEmails) ? validEmails.length : 1;
const invalidCount = Array.isArray(invalidEmails) ? invalidEmails.length : (invalidEmails.length > 0 ? 1 : 0);
const validRate = ((validCount / totalEmails) * 100).toFixed(2);

// Create summary report
const summary = {
  report_date: new Date().toISOString(),
  total_emails_processed: totalEmails,
  valid_emails: validCount,
  invalid_emails: invalidCount,
  validation_rate: `${validRate}%`,

  // Quality breakdown
  high_quality_emails: Array.isArray(validEmails) ? 
    validEmails.filter(email => email.email_score >= 90).length : 
    (validEmails.email_score >= 90 ? 1 : 0),

  medium_quality_emails: Array.isArray(validEmails) ? 
    validEmails.filter(email => email.email_score >= 70 && email.email_score < 90).length : 
    (validEmails.email_score >= 70 && validEmails.email_score < 90 ? 1 : 0),

  // Invalid reasons
  invalid_reasons: invalidEmails.length > 0 ? 
    [...new Set(Array.isArray(invalidEmails) ? 
      invalidEmails.map(email => email.validation_reason) : 
      [invalidEmails.validation_reason])].filter(Boolean) : [],

  // Files generated
  output_files: [
    `/tmp/valid-emails-${new Date().toISOString().split('T')[0]}.csv`,
    `/tmp/invalid-emails-${new Date().toISOString().split('T')[0]}.csv`
  ]
};

return summary;
