/*
 * Compile comprehensive company intelligence report
 */

const domain = $('ğŸ“ Input: Company Domain').first().json.domain;
const companyInfo = $('ğŸ¢ Get Company Info').first().json || {};
const fundingInfo = $('ğŸ’° Get Funding Data').first().json || {};
const ceoInfo = $('ğŸ‘” Find CEO').first().json || {};
const ctoInfo = $('ğŸ’» Find CTO').first().json || {};
const marketingInfo = $('ğŸ“¢ Find VP Marketing').first().json || {};
const googleAds = $('ğŸ“± Google Ads Intel').first().json || {};
const metaAds = $('ğŸ“˜ Meta Ads Intel').first().json || {};

// Helper to extract person data
const extractPerson = (data) => ({
  name: data.full_name || `${data.first_name || ''} ${data.last_name || ''}`.trim() || 'Not found',
  email: data.email || '',
  linkedin: data.profile_url || data.linkedin_url || '',
  title: data.job_title || data.title || ''
});

return {
  company: {
    domain: domain,
    name: companyInfo.name || companyInfo.company_name || domain.split('.')[0],
    industry: companyInfo.industry || 'Unknown',
    employees: companyInfo.employeesCount || companyInfo.employee_count,
    location: companyInfo.hq || companyInfo.location,
    founded: companyInfo.founded_year,
    description: companyInfo.shortDescription || companyInfo.description,
    linkedin_url: companyInfo.linkedin_url
  },
  funding: {
    total_raised: fundingInfo.total_funding || fundingInfo.funding_metrics?.total || 0,
    last_round: fundingInfo.last_round || fundingInfo.funding_metrics?.latest,
    last_amount: fundingInfo.last_amount,
    stage: fundingInfo.stage,
    investors: (fundingInfo.investors || []).slice(0, 10),
    valuation: fundingInfo.valuation
  },
  leadership: {
    ceo: extractPerson(ceoInfo),
    cto: extractPerson(ctoInfo),
    vp_marketing: extractPerson(marketingInfo)
  },
  advertising: {
    google_ads: {
      active: !!(googleAds.ads?.length || googleAds.count),
      count: googleAds.ads?.length || googleAds.count || 0,
      keywords: googleAds.keywords || [],
      ad_samples: (googleAds.ads || []).slice(0, 3)
    },
    meta_ads: {
      active: !!(metaAds.ads?.length || metaAds.count),
      count: metaAds.ads?.length || metaAds.count || 0,
      ad_samples: (metaAds.ads || []).slice(0, 3)
    }
  },
  meta: {
    researched_at: new Date().toISOString(),
    credits_used: 8,
    data_sources: [
      'company_search', 'funding_data', 
      'ceo_lookup', 'cto_lookup', 'vp_marketing_lookup',
      'google_ads', 'meta_ads'
    ]
  }
};
